import React from "react";
import classNames from "classnames";
import labelStyles from "./ProgressBarLabel.module.css";

import { indicatorStore } from "@/stores/IndicatorStore.js";
import { observer } from "mobx-react-lite";

const ProgressBarLabel = observer((props) => {
    const {
        position,
        label,
        showValue,
        labelPosition,
        dashboardSizeAlias,
        index = null,
    } = props;

    const {
        progressBarKeys: { color },
        indicatorSize,
    } = indicatorStore;

    if (!showValue || position !== labelPosition) {
        return null;
    }

    let labelUpdatedColor = {};

    if (index !== null) {
        labelUpdatedColor.color = color[index];
    }

    const labelClassName = classNames({
        [labelStyles.Label]: true,
        [labelStyles[indicatorSize]]: true,
    });

    return (
        <span className={labelClassName} style={labelUpdatedColor}>
            {label}
        </span>
    );
});
export default ProgressBarLabel;
