import React from "react";
import { observer } from "mobx-react-lite";
import { indicatorStore } from "@/stores/IndicatorStore.js";
import styles from "../ProgressBarConfiguration.module.scss";

export const IndicatorSize = observer((props) => {
    const { indicatorSize, changeIndicatorSize } = indicatorStore;

    const handleChange = (event) => {
        const selectedValue = event.target.value;
        changeIndicatorSize(selectedValue);
    };

    return (
        <select onChange={handleChange} className={styles["select-base"]}>
            <option value="large">Large</option>
            <option value="medium">Medium</option>
            <option value="small">Small</option>
        </select>
    );
});
