import React, { useState } from "react";
import { observer } from "mobx-react-lite";
import { indicatorStore } from "@/stores/IndicatorStore.js";
import styles from "../ProgressBarConfiguration.module.scss";

export const IndicatorType = observer((props) => {
    const [inputValue, setInputValue] = useState("");
    const {
        progressBarKeys: { indicatorType },
        changeProgressBarKey,
    } = indicatorStore;

    const handleChange = (event) => {
        const selectedValue = event.target.value;
        changeProgressBarKey("indicatorType", selectedValue);
    };

    return (
        <select onChange={handleChange} className={styles["select-base"]}>
            <option value="ClassicIndicator">Classic</option>
            <option value="BasicIndicator">Basic</option>
            <option value="MinimalisticIndicator">Minimalistic</option>
        </select>
    );
});
