import React, { useState } from "react";
import { observer } from "mobx-react-lite";
import { indicatorStore } from "@/stores/IndicatorStore.js";
import styles from "../ProgressBarConfiguration.module.scss";

export const InputCurrentValue = observer((props) => {
    const [inputValue, setInputValue] = useState("");
    const { currentValue, changeCurrentValue } = indicatorStore;

    const handleChange = (event) => {
        changeCurrentValue(Number(event.target.value));
    };

    return (
        <input
            type="number"
            value={currentValue}
            className={styles["input-field"]}
            onChange={handleChange}
        />
    );
});
