import React from "react";
import styles from "../ProgressBarConfiguration.module.scss";
import { observer } from "mobx-react-lite";
import { indicatorStore } from "@/stores/IndicatorStore.js";

export const RadioButtons = observer(({ propertyName }) => {
    const {
        progressBarKeys: { [propertyName]: value },
        changeProgressBarKey,
    } = indicatorStore;

    const handleChange = (newValue) => {
        changeProgressBarKey(propertyName, newValue);
    };

    return (
        <div className={styles.radioGroup}>
            <label className={styles.radioOption}>
                <input
                    type="radio"
                    name={propertyName}
                    value="top"
                    checked={value === "top"}
                    onChange={() => handleChange("top")}
                />
                <span>Top</span>
            </label>

            <label className={styles.radioOption}>
                <input
                    type="radio"
                    name={propertyName}
                    value="bottom"
                    checked={value === "bottom"}
                    onChange={() => handleChange("bottom")}
                />
                <span>Bottom</span>
            </label>
        </div>
    );
});
