import React from "react";
import { observer } from "mobx-react-lite";
import { indicatorStore } from "@/stores/IndicatorStore.js";
import styles from "../ProgressBarConfiguration.module.scss";

export const ShowValueCheckbox = observer((props) => {
    const { propertyKey, label } = props;
    const { progressBarKeys, changeProgressBarKey } = indicatorStore;
    const showValue = progressBarKeys[propertyKey];

    const handleChange = (event) => {
        changeProgressBarKey(propertyKey, event.target.checked);
    };

    return (
        <div>
            <label className={styles["checkbox-container"]}>
                <input
                    type="checkbox"
                    checked={showValue}
                    onChange={handleChange}
                />
                {label}
            </label>
        </div>
    );
});
